{
    "session": "{{session}}",
    "id": 1,
    "method": "set",
    "params": [
        {
            "url": "/dvmdb/adom/{{adom}}/script",
            "data": [
                {
                    "script_schedule": null,
                    "name": "Configure FAZ",
                    "type": "cli",
                    "desc": "",
                    "content": "# FAZ Configuration
config log fortianalyzer setting
  set status enable
  set server 192.168.0.15
  set upload-option realtime
  set serial {{sn_fmg}}
  set certificate-verification disable
  set reliable enable
end",
                    "target": "device_database"
                }
            ]
        }
    ]
}

