{
    "session": "{{session}}",
    "id": 1,
    "method": "set",
    "params": [
        {
            "url": "/dvmdb/adom/{{adom}}/script",
            "data": [
                {
                    "script_schedule": null,
                    "name": "Configure FAZ",
                    "type": "cli",
                    "desc": "",
                    "content": "# FAZ Configuration
config log fortianalyzer setting
  set status enable
  set server 192.168.0.15
  set upload-option realtime
  set serial {{sn_fmg}}
  set certificate-verification disable
  set reliable enable
end",
                    "target": "device_database"
                }
            ]
        }
    ]
}

{
    "session": "{{session}}",
    "id": 1,
    "method": "set",
    "params": [
        {
            "url": "/dvmdb/adom/{{adom}}/script",
            "data": [
                {
                    "script_schedule": null,
                    "name": "lo-HC",
                    "type": "cli",
                    "desc": "",
                    "content": "# Health-check Loopback
config system interface
  edit \"lo-HC\"
    set vdom \"root\"
    set ip 10.200.99.1 255.255.255.255
    set allowaccess ping
    set type loopback
  next
end",
                    "target": "device_database"
                }
            ]
        }
    ]
}

